Action.Str:
function pad(n){ return String(n).padStart(2,'0'); }

function isoWithLocalOffset(ms){
  ms = Number(ms);
  if (!ms) return "";
  var d = new Date(ms);
  var tzMin = -d.getTimezoneOffset();
  var sign = tzMin >= 0 ? "+" : "-";
  var hh = pad(Math.floor(Math.abs(tzMin) / 60));
  var mm = pad(Math.abs(tzMin) % 60);
  return d.getFullYear() + "-" + pad(d.getMonth()+1) + "-" + pad(d.getDate()) +
    "T" + pad(d.getHours()) + ":" + pad(d.getMinutes()) + ":" + pad(d.getSeconds()) +
    sign + hh + ":" + mm;
}

// Arrays from Get Calendar Events
var titles = (typeof ce_title !== 'undefined') ? ce_title : [];
var starts = (typeof ce_start_time !== 'undefined') ? ce_start_time : [];
var ends = (typeof ce_end_time !== 'undefined') ? ce_end_time : [];
var descs = (typeof ce_description !== 'undefined') ? ce_description : [];
var locs = (typeof ce_location !== 'undefined') ? ce_location : [];
var allDay = (typeof ce_all_day !== 'undefined') ? ce_all_day : [];
var visible = (typeof ce_visible !== 'undefined') ? ce_visible : [];
var eventId = (typeof ce_event_id !== 'undefined') ? ce_event_id : [];
var tzs = (typeof ce_timezone !== 'undefined') ? ce_timezone : [];
var account = (typeof ce_account !== 'undefined') ? ce_account : [];


// filename with date
var now = new Date();
var ymd = now.getFullYear() + "-" + pad(now.getMonth()+1) + "-" + pad(now.getDate());
var filename = "outlook_" + ymd + ".json";

var events = [];
var n = Math.max(titles.length, starts.length, ends.length, descs.length, locs.length, allDay.length, visible.length, eventId.length, tzs.length);

for (var i=0; i<n; i++){
  events.push({
    title: (titles[i] ?? "").toString(),
    start: isoWithLocalOffset(starts[i]),
    end: isoWithLocalOffset(ends[i]),
    allDay: String(allDay[i] ?? "false") === "true",
    location: (locs[i] ?? "").toString(),
    description: (descs[i] ?? "").toString(),
    visible: String(visible[i] ?? "true") === "true",
    eventId: (eventId[i] ?? "").toString(),
    timezone: (tzs[i] ?? "").toString(),
sourceCalendar: (account[i] ?? "").toString()
  });
}

var eventsjson = JSON.stringify(events, null, 2);
